<p>这个页面收录我喜欢的歌曲和我（Maybe more?）唱过的歌曲.</p>
<p>emm，就这样吧~</p>


<link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/aplayer/dist/APlayer.min.css">
<script src="https://cdn.jsdelivr.net/npm/aplayer/dist/APlayer.min.js"></script>

<div id="aplayer"></div>
<script>
  document.addEventListener('DOMContentLoaded', function() {
    
    var names = '大耳朵爷爷历险记片尾曲,特别的人,最后一页,青花瓷,红尘客栈'.replace(/['"]/g, '').split(',');
    var urls = '大耳朵爷爷主题曲.mp3,特别的人.mp3,最后一页.mp3,青花瓷.mp3,红尘客栈.mp3'.replace(/['"]/g, '').split(',');
    var covers = 'grandpa.jpg,1.jpg,2.jpg,3.jpg,4jpg'.replace(/['"]/g, '').split(',');
    var artists = '谷红,Jin\u003c2024-09-05\u003e,Jin\u003c2024-09-17\u003e,Leo\u003c2024-09-17\u003e,Leo\u003c2024-09-20\u003e'.replace(/['"]/g, '').split(',');
    
    console.log(names, urls, covers, artists);
    var ap = new APlayer({
      element: document.getElementById('aplayer'),
      narrow: false,
      autoplay: false,
      theme: '#b7daff',
      loop: 'all',
      order: 'list',
      preload: 'auto',
      audio: names.map(function(name, index) {
        return {
          name: name.trim(),
          artist: artists[index] ? artists[index].trim() : '',
          url: urls[index] ? urls[index].trim() : '',
          cover: covers[index] ? covers[index].trim() : ''
        };
      })
    });
  });
</script>
