<script>
  document.addEventListener('DOMContentLoaded', function() {
    
    var names = 'å¤§è€³æœµçˆ·çˆ·å†é™©è®°ç‰‡å°¾æ›²,ç‰¹åˆ«çš„äºº,æœ€åä¸€é¡µ,é’èŠ±ç“·,çº¢å°˜å®¢æ ˆ'.replace(/['"]/g, '').split(',');
    var urls = 'å¤§è€³æœµçˆ·çˆ·ä¸»é¢˜æ›².mp3,ç‰¹åˆ«çš„äºº.mp3,æœ€åä¸€é¡µ.mp3,é’èŠ±ç“·.mp3,çº¢å°˜å®¢æ ˆ.mp3'.replace(/['"]/g, '').split(',');
    var covers = 'grandpa.jpg,1.jpg,2.jpg,3.jpg,4jpg'.replace(/['"]/g, '').split(',');
    var artists = 'è°·çº¢,Jin\u003c2024-09-05\u003e,Jin\u003c2024-09-17\u003e,Leo\u003c2024-09-17\u003e,Leo\u003c2024-09-20\u003e'.replace(/['"]/g, '').split(',');
    
    console.log(names, urls, covers, artists);
    var ap = new APlayer({
      element: document.getElementById('aplayer'),
      narrow: false,
      autoplay: false,
      theme: '#b7daff',
      loop: 'all',
      order: 'list',
      preload: 'auto',
      audio: names.map(function(name, index) {
        return {
          name: name.trim(),
          artist: artists[index] ? artists[index].trim() : '',
          url: urls[index] ? urls[index].trim() : '',
          cover: covers[index] ? covers[index].trim() : ''
        };
      })
    });
  });
</script>
